template.script:
function validNumber() {
let x = document.getElementById("phone_number");
var error = document.getElementById("err")
if (x.value == "") {
error.style.display = "block";
error.textContent = "أدخل رقم التواصل"
error.style.color = "red"
setTimeout(() => {
error.style.display = 'none';
}, 3000);
} else if (x.value.length < 10) {
error.style.display = "block";
error.textContent = "تأكد من أنهم تسعة ارقام"
error.style.color = "red"
} else {
error.style.display = "none";
error.textContent = "0000000"
}
}

document.addEventListener('DOMContentLoaded', function() {
var selectElement = document.getElementById('type');
var detailsLabel = document.getElementById('desc');
var cityDiv = document.getElementById('city-div');
selectElement.addEventListener('change', function() {
if (selectElement.value === 'online_shop') {
detailsLabel.textContent = 'نبذة عن المتجر';
cityDiv.style.display = 'none';

} else {
detailsLabel.textContent = 'تفاصيل الفروع ان وجدت';
cityDiv.style.display = 'block';
}
});
});
